// This code goes in the "Prepare Sites" node
// Make sure this node has inputs from BOTH:
// - "Get Current IP" 
// - "Get Existing DNS Records"

const domain = 'alex-it.net';
const zoneID = '3462e9f3ae9f12d67018a49274db3def';

// Get all inputs and debug what we're receiving
const allInputs = $input.all();
console.log('All inputs received:', allInputs.map(item => Object.keys(item.json)));

// Find current IP (from Get Current IP node)
const ipInput = allInputs.find(item => item.json && item.json.ip);
const currentIP = ipInput ? ipInput.json.ip : null;

// Find DNS records (from Get Existing DNS Records node) 
const dnsInput = allInputs.find(item => item.json && item.json.result);
const dnsResponse = dnsInput ? dnsInput.json : null;

console.log('Current IP found:', currentIP);
console.log('DNS response found:', !!dnsResponse);

if (!currentIP) {
  throw new Error('Could not find current IP in inputs');
}

if (!dnsResponse || !dnsResponse.success || !dnsResponse.result) {
  console.log('Warning: Could not fetch existing DNS records, using fallback list');

  // Fallback to your original hardcoded list
  const fallbackSites = [
    { subdomain: 'grafana' },
    { subdomain: 'ha' },
    { subdomain: 'it-assets' },
    { subdomain: 'n8n' },
    { subdomain: 'portainer' },
    { subdomain: 'vault' },
    { subdomain: '8n8.pi' },
    { subdomain: 'pihole' },
    { subdomain: 'portainer.pi' }
  ];

  return [{
    json: {
      domain,
      zoneID,
      newIP: currentIP,
      sites: fallbackSites.map(s => ({ ...s, fqdn: `${s.subdomain}.${domain}` }))
    }
  }];
}

// Extract subdomains from existing A records
const sites = dnsResponse.result
  .filter(record => record.name.endsWith(`.${domain}`) || record.name === domain)
  .map(record => {
    const subdomain = record.name === domain ? '@' : record.name.replace(`.${domain}`, '');
    return { subdomain };
  });

// Add any new subdomains you want to always include (optional)
const alwaysInclude = [
  // { subdomain: 'new-service' }, // Uncomment and add new services here
];

// Merge existing and new subdomains (remove duplicates)
const allSites = [...sites, ...alwaysInclude.filter(newSite => 
  !sites.some(existingSite => existingSite.subdomain === newSite.subdomain)
)];

console.log(`Found ${allSites.length} sites to manage:`, allSites.map(s => s.subdomain));

return [{
  json: {
    domain,
    zoneID,
    newIP: currentIP,
    sites: allSites.map(s => ({ 
      ...s, 
      fqdn: s.subdomain === '@' ? domain : `${s.subdomain}.${domain}` 
    }))
  }
}];

// Fan out one item per FQDN, attach zoneID
const original = $input.first().json;

if (!original || !Array.isArray(original.sites)) throw new Error('sites missing or not an array');

return original.sites.map(site => ({
  json: {
    subdomain: site.subdomain,
    fqdn: site.fqdn,
    domain: original.domain,
    newIP: original.newIP,
    zoneID: original.zoneID
  }
}));

// Merge Cloudflare response with original site data using paired items
const cfItems = $input.all();
return cfItems.map((item, i) => {
  const pairedIndex = (item.pairedItem && item.pairedItem.item != null) ? item.pairedItem.item : i;
  const original = $items('Expand Sites')[pairedIndex].json;
  const record = item.json?.result?.[0] || {};

  return {
    json: {
      subdomain: original.subdomain,
      fqdn: original.fqdn,
      domain: original.domain,
      newIP: original.newIP,
      zoneID: original.zoneID,
      recordId: record.id || '',
      existingIP: record.content || '',
      existingTTL: record.ttl != null ? record.ttl : 1,
      existingProxied: record.proxied != null ? record.proxied : false
    }
  };
});

// Evaluate each incoming item (one per FQDN)
const items = $input.all();

const desiredTTL = 1;         // Cloudflare "Auto"
const desiredProxied = false; // DNS only

return items.map(item => {
  const j = item.json;
  const hasRecord = !!j.recordId;

  const needsUpdate =
    !hasRecord ||
    j.existingIP !== j.newIP ||
    j.existingTTL !== desiredTTL ||
    j.existingProxied !== desiredProxied;

  console.log(
    `[${j.fqdn}] ${j.existingIP || '<none>'} -> ${j.newIP} | ttl:${j.existingTTL} proxied:${j.existingProxied} | update? ${needsUpdate}`
  );

  return {
    json: { ...j, hasRecord, needsUpdate, desiredTTL, desiredProxied }
  };
});

return $input.all().map(item => {
  const res = item.json || {};
  const fqdn = (res.result && res.result.name) || res.name || 'unknown';
  const success = res.success === true;
  const error = success ? undefined : ((res.errors && res.errors[0] && res.errors[0].message) || res.message || 'Unknown error');
  return { json: { success, action: 'updated', fqdn, error } };
});

return $input.all().map(item => {
  const res = item.json || {};
  const fqdn = (res.result && res.result.name) || res.name || 'unknown';
  const success = res.success === true;
  const error = success ? undefined : ((res.errors && res.errors[0] && res.errors[0].message) || res.message || 'Unknown error');
  return { json: { success, action: 'created', fqdn, error } };
});

const results = $input.all();
const timestamp = new Date().toISOString();
let created = 0, updated = 0, noChange = 0, failed = 0;

const createdFQDNs = [];
const updatedFQDNs = [];

for (const item of results) {
  const r = item.json;
  if (!r || r.success === false) {
    failed++;
    continue;
  }

  if (r.action === 'created') {
    created++;
    createdFQDNs.push(r.fqdn);
  } else if (r.action === 'updated') {
    updated++;
    updatedFQDNs.push(r.fqdn);
  } else if (r.action === 'no_change') {
    noChange++;
  }
}

console.log(`DNS Summary ${timestamp} -> created: ${created}, updated: ${updated}, no change: ${noChange}, failed: ${failed}`);

return [{
  json: {
    timestamp,
    created,
    updated,
    noChange,
    failed,
    total: results.length,
    createdFQDNs,
    updatedFQDNs
  }
}];

const timestamp = new Date().toLocaleString("en-GB", {
  timeZone: "Asia/Jerusalem",
  hour12: false
}).replace(",", "");

// ðŸ“¦ Base summary line
const summary = 'ðŸ›°ï¸ DNS Summary ' + timestamp;

// ðŸ” Updated domains with IP diff
const updatedText = ($json["updatedFQDNs"] && $json["updatedFQDNs"].length > 0)
  ? "\n\n:repeat: Updated domains with new IPs:\n" +
    $json["updatedFQDNs"]
      .map(fqdn => {
        const match = $items('Prepare Payload').find(i => i.json.fqdn === fqdn);
        const newIP = match?.json?.newIP || 'unknown';
        const oldIP = match?.json?.existingIP || 'unknown';
        return `${fqdn} updated to ${newIP} from old IP ${oldIP}`;
      }).join("\n")
  : "";

// ðŸ†• Created domains
const createdText = ($json["createdFQDNs"] && $json["createdFQDNs"].length > 0)
  ? "\n\n:new: Created domains:\n" + $json["createdFQDNs"].join("\n")
  : "";

// âœ… No change message
if ($json["created"] === 0 && $json["updated"] === 0 && $json["failed"] === 0) {
  return [{
    json: {
      message: `âœ… DNS check completed at ${timestamp} â€” no changes detected.`
    }
  }];
}

// ðŸ“© Final formatted Slack message
return [{
  json: {
    message: summary + updatedText + createdText
  }
}];

