  { subdomain: 'portainer' },
  { subdomain: 'vault' },
  { subdomain: '8n8.pi' },
  { subdomain: 'pihole' },
  { subdomain: 'portainer.pi' }
];

const domain = 'alex-it.net';
const currentIP = $input.first().json.ip;
const zoneID = '3462e9f3ae9f12d67018a49274db3def'; // <-- your zone id

