// Process each input item
return items.map((item, index) => {
  const body = item.json.body || item.json;
  const event = body.event || {};
  const headers = item.json.headers || {};

  // Add debug logging
  console.log('Received event type:', body.type);
  console.log('Event details:', JSON.stringify(event, null, 2));

  // 1) Handle URL verification challenge
  if (body.type === 'url_verification') {
    console.log('URL verification challenge');
    return {
      json: { challenge: body.challenge },
      pairedItem: { item: index }
    };
  }

  // 2) Skip Slack retries
  if (headers['x-slack-retry-num'] || headers['x-slack-retry-reason']) {
    console.log('Skipping Slack retry');
    return null; // Skip this item
  }

  // 3) Skip if not a message event or if it's a bot message
  if (event.type !== 'message' || event.subtype === 'bot_message' || event.bot_id) {
    console.log('Not a user message, skipping');
    return null; // Skip this item
  }

  // 4) Skip messages without text
  if (!event.text) {
    console.log('No text in message');
    return null; // Skip this item
  }

  // 5) Skip messages from your bot (replace with actual bot user ID)
  const BOT_USER_ID = 'U07K8QJ9XYZ'; // Replace with your bot's user ID
  if (BOT_USER_ID && event.user === BOT_USER_ID) {
    console.log('Ignoring message from bot');
    return null; // Skip this item
  }

  // 6) Extract URLs from message
  const text = event.text || "";
  const urls = new Set();

  // Slack format: <http://example.com|label> or <http://example.com>
  const slackUrlRegex = /<(https?:\/\/[^>|]+)(\|[^>]+)?>/g;
  let match;
  while ((match = slackUrlRegex.exec(text)) !== null) {
    urls.add(match[1]);
  }

  // Plain URLs
  const plainUrlRegex = /https?:\/\/[^\s<>"'()[\]{}]+/g;
  while ((match = plainUrlRegex.exec(text)) !== null) {
    urls.add(match[0]);
  }

  // Get first valid URL
  let firstUrl = '';
  for (const url of urls) {
    if (url && url.match(/^https?:\/\/.+\..+/)) {
      firstUrl = url;
      break;
    }
  }

  if (!firstUrl) {
    console.log('No valid URL found in message:', text);
    return null; // Skip this item
  }

  console.log('Found URL:', firstUrl);

  return {
    json: {
      url: firstUrl,
      text: text,
      slack_channel: event.channel,
      slack_user: event.user,
      slack_ts: event.ts,
      slack_thread_ts: event.thread_ts,
      event_id: body.event_id || `${event.ts}-${Date.now()}`
    },
    pairedItem: { item: index }
  };
}).filter(item => item !== null); // Remove null items (skipped items)

// Debug: let's see what data we have
const inputData = $input.first().json;
console.log('Available fields:', Object.keys(inputData));

// Try different possible field names for HTML content
const html = inputData.data || 
             inputData.html || 
             inputData.body || 
             inputData.content || 
             inputData.response || 
             "";

console.log('HTML length:', html.length);

if (!html) {
    return { 
        error: 'No HTML content found',
        availableFields: Object.keys(inputData)
    };
}

// Simple extraction
const MAX_LENGTH = 8000; // Reduced for safety

// Extract paragraphs and headings
const matches = html.match(/<(p|h[1-6])[^>]*>(.*?)<\/\1>/gis);

let text = 'No content found.';
if (matches) {
    text = matches
        .map(m => m.replace(/<[^>]+>/g, '').trim())
        .filter(t => t.length > 5)
        .slice(0, 20) // Limit to first 20 matches for safety
        .join('\n\n');
}

// Truncate if too long
if (text.length > MAX_LENGTH) {
    text = text.substring(0, MAX_LENGTH) + '... [truncated]';
}

return { 
    article: text,
    originalLength: html.length,
    extractedLength: text.length
};

const source = ($json.source ?? "Local").toString();
const raw = ($json.text || $json.output || $json.content || "").toString();

// ---------- CLEANUP ----------
let s = raw
  .replace(/<think>[\s\S]*?<\/think>/gi, "")
  .replace(/```(?:json)?\s*([\s\S]*?)```/gi, "$1")
  .trim();

let obj = null;
try { obj = JSON.parse(s); } catch {}
if (!obj) {
  const match = s.match(/\{[\s\S]*\}/);
  if (match) {
    try { obj = JSON.parse(match[0]); } catch {}
  }
}

// ---------- EXTRACT ----------
let title = "";
let bullets = [];

if (obj && typeof obj === "object") {
  if (typeof obj.title === "string") {
    title = obj.title.trim().slice(0, 80);
  }
  if (Array.isArray(obj.bullets)) {
    bullets = obj.bullets
      .map(b => (typeof b === "string" ? b.trim() : ""))
      .filter(Boolean);
  }
}

// ---------- FALLBACK ----------
if (!title) title = "Summary";

// ---------- ENFORCE 5 BULLETS ----------
if (bullets.length > 5) {
  bullets = bullets.slice(0, 5);
}
while (bullets.length < 5) {
  bullets.push("No further details available.");
}

// ---------- OUTPUT ----------
return [{
  json: {
    title,
    bullets,
    source
  }
}];

const { title, bullets, error, raw, url } = $json;

if (error) {
  return [{
    json: {
      text: `âš ï¸ ${error}\n\n\`\`\`\n${raw?.slice(0, 500) || "No raw content"}\n\`\`\``
    }
  }];
}

// Clean the title
let cleanTitle = String(title || "Summary")
  .replace(/^[^{"]*"?title["']?\s*[:=â€“-]\s*/i, "")
  .replace(/^["']|["']$/g, "")
  .replace(/[.,"]+$/, "")
  .trim();

// Remove footer from title if accidentally included
cleanTitle = cleanTitle.replace(/automated with this n8n workflow/i, "").trim();

// Clean bullets
const cleanBullets = (Array.isArray(bullets) ? bullets : [])
  .map(b => String(b).trim())
  .filter(b => !/^["']?title["']?\s*[:=â€“-]/i.test(b))  // STRONG title bullet filter
  .filter(b => !/automated with this n8n workflow/i.test(b))
  .map(b => {
    const cleaned = b.replace(/^["']|["']$/g, "").replace(/[.,"]+$/, "").trim();
    return cleaned.endsWith(".") ? cleaned : `${cleaned}.`;
  })
  .filter(Boolean)
  .slice(0, 5);

// Build final Slack message
const parts = [
  `*ðŸ§  Summary:* ${url ? `<${url}|${cleanTitle}>` : `*${cleanTitle}*`}`,
  "",
  ...cleanBullets.map(b => `â€¢ ${b}`),
  ""
];

const text = parts.join("\n");

return [{ json: { text } }];
