const html = $json["data"] || "";

// try to scope to the main <article>, else fall back to whole HTML
const scope = (html.match(/<article[\s\S]*?<\/article>/i) || [html])[0];

// capture paragraphs, headings, and list items
const matches = scope.match(/<(p|h1|h2|li)[^>]*>(.*?)<\/\1>/gis);

let text = matches
  ? matches
      .map(m => m.replace(/<[^>]+>/g, '').trim())
      .filter(Boolean)
      .join('\n\n')
  : 'No article content found.';

return [{ json: { article: text } }];

const source = ($json.source ?? "Local").toString();
const raw = ($json.text || $json.output || $json.content || "").toString();

// ðŸ§¼ ---------- CLEANUP ----------
let s = raw
  .replace(/<think>[\s\S]*?<\/think>/gi, "")
  .replace(/```(?:json)?\s*([\s\S]*?)```/gi, "$1")
  .replace(/^\s*:warning:.*$/gmi, "")
  .replace(/^\s*(?:IT Onboarding Bot|APP\b).*$\n?/gmi, "")
  .replace(/^\s*Automated\s*â€¢.*$/gmi, "")
  .replace(/^\s*text\s*$/gmi, "")
  .replace(/^\s*[{}]\s*$/gm, "")
  .replace(/,\s*}/g, "}")
  .replace(/,\s*]/g, "]")
  .trim();

// ðŸ§ª DEBUG LOG
console.log("ðŸ” Cleaned input:\n", s);

// ðŸ§  ---------- TRY JSON ----------
let obj = null;
try { obj = JSON.parse(s); } catch {}
if (!obj) {
  const match = s.match(/\{[\s\S]*\}/);
  if (match) {
    try { obj = JSON.parse(match[0]); } catch {}
  }
}

// ðŸ§  ---------- EXTRACT FIELDS ----------
let title = "";
let bullets = [];

if (obj && typeof obj === "object") {
  title = (obj.title || "").toString().trim().slice(0, 80);
  if (Array.isArray(obj.bullets)) {
    bullets = obj.bullets.map(b => (b ?? "").toString().trim()).filter(Boolean);
  }
}

// ðŸª‚ ---------- FALLBACK ----------
if (!title || bullets.length === 0) {
  const lines = s.split(/\r?\n/).map(l => l.trim()).filter(Boolean);

  if (!title) {
    const tLine = lines.find(l => /^title\s*[:\-]\s*/i.test(l));
    title = (tLine
      ? tLine.replace(/^title\s*[:\-]\s*/i, "")
      : lines.find(l => !/^(?:[-*â€¢]|\d+\.)\s+/.test(l)) ||
        (bullets[0] ? bullets[0].split(/[.!?]\s/)[0] : "Summary")
    ).toString().trim().slice(0, 80);
  }

  if (bullets.length === 0) {
    bullets = lines
      .filter(l => /^(?:[-*â€¢]|\d+\.)\s+/.test(l))
      .map(l => l.replace(/^(?:[-*â€¢]|\d+\.)\s+/, "").trim())
      .filter(Boolean);

    // If still no bullets, take long lines as fallback
    if (bullets.length === 0) {
      bullets = lines
        .filter(l => l.length > 30)
        .slice(0, 5)
        .map(l => l.replace(/^(?:[-*â€¢]|\d+\.)\s+/, "").trim());
    }
  }
}

// ðŸ§¹ ---------- NORMALIZE ----------
bullets = bullets
  .map(b => b.replace(/^\*\*(.+?)\*\*$/, "$1").trim())
  .filter(Boolean)
  .slice(0, 5);

// ðŸ§¾ ---------- OUTPUT ----------
if (!title && bullets.length === 0) {
  return [{
    json: {
      error: "No summary content",
      source,
      raw: s
    }
  }];
}

return [{
  json: {
    title: title || "Summary",
    bullets,
    source
  }
}];

const { title, bullets, error, raw } = $json;

if (error) {
  return [{
    json: {
      text: `âš ï¸ ${error}\n\n\`\`\`\n${raw?.slice(0, 500) || "No raw content"}\n\`\`\``
    }
  }];
}

// Clean the title: strip quotes and any 'title:' prefix
const cleanTitle = String(title || "Summary")
  .replace(/^["']?title["']?\s*[:\-]\s*/i, "")
  .replace(/^["']|["']$/g, "")
  .trim();

// Clean bullets: strip surrounding quotes and redundant 'title' lines
const cleanBullets = (Array.isArray(bullets) ? bullets : [])
  .map(b => String(b).trim())
  .filter(b => !/^["']?title["']?\s*[:\-]/i.test(b)) // remove if line is title again
  .map(b => b.replace(/^["']|["']$/g, ""))           // remove surrounding quotes
  .filter(Boolean)
  .slice(0, 5); // max 5

const text = [cleanTitle, ...cleanBullets.map(b => `- ${b}`)].join("\n");
return [{ json: { text } }];
