// Parse PowerShell output and extract model name
const input = $input.first();
const powershellOutput = input.json.stdout;
const serialNumber = input.json.serialNumber;
const itemId = input.json.itemId;
const itemName = input.json.itemName;
const modelColumnId = input.json.modelColumnId;

let modelName = '';

// Clean up the PowerShell output
if (powershellOutput) {
  // Remove any extra whitespace and newlines
  modelName = powershellOutput.trim();

  // Remove common PowerShell artifacts
  modelName = modelName.replace(/^\s*\n/gm, '');
  modelName = modelName.replace(/\s+/g, ' ');

  // Remove any trailing dots or special characters
  modelName = modelName.replace(/\.$/, '');

  // If empty or contains error messages, set to 'Unknown'
  if (!modelName || 
      modelName.includes('Error') || 
      modelName.includes('Exception') ||
      modelName.includes('not found') ||
      modelName.includes('Cannot') ||
      modelName.length < 3) {
    modelName = 'Model not found';
  }
}
else {
  modelName = 'No output from script';
}

console.log(`Item: ${itemName} | Serial: ${serialNumber} | Model: ${modelName}`);

return {
  itemId: itemId,
  itemName: itemName,
  serialNumber: serialNumber,
  modelName: modelName,
  modelColumnId: modelColumnId
};

// Log the update result
const input = $input.first();
const itemName = input.json.itemName || 'Unknown item';
const modelName = input.json.modelName || 'N/A';
const itemId = input.json.itemId || 'unknown';

console.log(`‚úÖ Updated: ${itemName} (ID: ${itemId}) | Model: ${modelName}`);

// Return data for batch processing to continue
return {
  success: true,
  itemId: itemId,
  itemName: itemName,
  modelName: modelName,
  timestamp: new Date().toISOString()
};

// Final summary of all processed items
const allItems = $input.all();
const totalProcessed = allItems.length;
const successfulUpdates = allItems.filter(item => item.json.success).length;

console.log(`\n=== PROCESSING COMPLETE ===`);
console.log(`Total items processed: ${totalProcessed}`);
console.log(`Successful updates: ${successfulUpdates}`);
console.log(`Failed updates: ${totalProcessed - successfulUpdates}`);
console.log(`========================\n`);

return {
  summary: {
    totalProcessed: totalProcessed,
    successfulUpdates: successfulUpdates,
    failedUpdates: totalProcessed - successfulUpdates,
    completedAt: new Date().toISOString()
  },
  processedItems: allItems.map(item => ({
    itemId: item.json.itemId,
    itemName: item.json.itemName,
    modelName: item.json.modelName,
    success: item.json.success
  }))
};

