      itemId: response.data.change_multiple_column_values.id
    }
  }];
} else {
  return [{
    json: {
      status: "ERROR", 
      message: "Update may have failed",
      response: response
    }
  }];
}

// Summary of all processed devices
const allExecutions = $input.all();
const summary = {
  totalProcessed: allExecutions.length,
  devices: allExecutions.map(exec => ({
    serial: exec.json.serialNumber || 'unknown',
    status: exec.json.status || 'unknown',
    itemId: exec.json.itemId || 'unknown'
  }))
};

console.log('=== FINAL SUMMARY ===');
console.log(`Processed ${summary.totalProcessed} devices`);
summary.devices.forEach(device => {
  console.log(`${device.serial}: ${device.status} (Item: ${device.itemId})`);
});

return [{ json: summary }];
