const items = $input.all();
const itemsToProcess = [];
const skippedDevices = [];
const notFoundDevices = [];

function getDeviceType(serial) {
  if (serial.startsWith('C02') && serial.length >= 10) return 'Apple';
  if (serial.startsWith('CND')) return 'Dell';
  if (serial.match(/^(PC|PF)/)) return 'Lenovo';
  if (serial.startsWith('R914')) return 'Lenovo';
  if (serial.match(/^\d+[A-Z]+$/)) return 'Lenovo';
  if (serial.startsWith('MP')) return 'Unknown';
  return 'Unknown';
}

