items.forEach(item => {
  const serial = item.json.name?.trim();
  if (serial) {
    mondayItemsMap.set(serial, item);
  }
});

console.log(`ðŸ“‹ Found ${mondayItemsMap.size} total items in Monday.com`);

// Process each target serial
targetSerials.forEach(targetSerial => {
  const item = mondayItemsMap.get(targetSerial);

