// Parse Apple API response - PowerShell is returning ASCII codes, not text
let rawResponse = $json.stdout;

const itemId = $('Filter Target Items Mac').item.json.itemId;
const serialNumber = $('Filter Target Items Mac').item.json.serialNumber;

console.log(`Processing ${serialNumber} for item ${itemId}`);

// Convert ASCII decimal codes to actual text
// The stdout contains numbers separated by \r\n
const asciiCodes = rawResponse.split('\r\n').filter(code => code.trim() !== '');
const xmlText = asciiCodes.map(code => String.fromCharCode(parseInt(code))).join('');

console.log('Converted XML:', xmlText);

let modelName = 'Unknown Model';

// Check for error in XML
if (xmlText.includes('<error>')) {
  const errorMatch = xmlText.match(/<error>(.*?)<\/error>/);
  console.log(`Apple API returned error: ${errorMatch ? errorMatch[1] : 'unknown'}`);
  return {
    itemId,
    serialNumber,
    Name: 'Model Not Found (API Error)',
    error: errorMatch ? errorMatch[1] : 'API error'
  };
}

// Extract model name from configCode tag
const configMatch = xmlText.match(/<configCode>(.*?)<\/configCode>/);

