// Helper function to determine model from serial number patterns
function getModelFromSerial(serial) {
  if (!serial) return 'Mac';

  const upper = serial.toUpperCase();

  // Get last 4 characters (model identifier)
  const modelCode = upper.substring(upper.length - 4);

  // Common MacBook Air identifiers
  const airCodes = [
    '7H5G', 'FXQ0', '2VQ0', 'LWQ0', 'FAQ0', 'Q05P', 'SHV2', '40CQ',
    '99KQ', 'PQWN', 'C5ML', 'QXTR', 'M072', '6K1', 'BXQ0', 'PL4M',
    'NHJ', 'MQ1J', 'NY67', 'LVFP', 'XNKP', 'YMQF', 'X63C', 'M6KQ',
    '0CQ0'
  ];

  // Common MacBook Pro identifiers (12-char serials typically Pro)
  const proCodes = [
    'MD6M', 'ML85', 'ML87', 'ML7H', 'Q05D', 'JHD2', 'HV29', 'LVDG',
    'LVCJ', 'LVDL', 'P3Y1', 'HV2V', 'HV2Q', 'LVDC', 'JHCD', 'HV2D',
    'LVDM', 'WT'
  ];

  // Check against known patterns
  if (airCodes.includes(modelCode)) {
    return 'MacBook Air';
  }

  if (proCodes.includes(modelCode)) {
    return 'MacBook Pro';
  }

  // Fallback patterns based on serial structure
  // 12-character serials (C02...) are typically MacBook Pro
  if (upper.startsWith('C02') && upper.length === 12) {
    return 'MacBook Pro';
  }

  // 10-character serials starting with FV are typically MacBook Air
  if (upper.startsWith('FV') && upper.length === 12) {
    return 'MacBook Air';
  }

  // VQH prefix is often MacBook Pro
  if (upper.startsWith('VQH')) {
    return 'MacBook Pro';
  }

  // Default fallback
  return 'Mac';
}

