  // PowerShell returned ASCII codes - convert them
  console.log('Converting ASCII codes to text...');
  const asciiCodes = rawResponse.split('\r\n').filter(code => code.trim() !== '');
  xmlText = asciiCodes.map(code => String.fromCharCode(parseInt(code))).join('');
} else {
