// Parse Lenovo API response
const lenovoData = JSON.parse($json.stdout);

// Get the itemId and serialNumber from Filter Target Items1 node
const itemId = $('Filter Target Items Lenovo').item.json.itemId;
const serialNumber = $('Filter Target Items Lenovo').item.json.serialNumber;

console.log(`Processing ${serialNumber} for item ${itemId}`);

let bestResult = null;

if (Array.isArray(lenovoData) && lenovoData.length > 0) {
  bestResult = lenovoData.reduce((best, current) => {
    const currentName = current.Name || '';
    const bestName = best ? (best.Name || '') : '';

    if (currentName.includes('THINKPAD') || currentName.includes('IDEAPAD') || currentName.includes('YOGA')) {
      return current;
    }

    if (currentName.length > bestName.length) {
      return current;
    }

    return best || current;
  }, null);

} else if (lenovoData && !Array.isArray(lenovoData)) {
  bestResult = lenovoData;
} else {
  console.log(`No valid data from Lenovo API for ${serialNumber}`);
  return {
    itemId,
    serialNumber,
    Name: 'Model Not Found',
    error: 'No data from Lenovo API'
  };
}

// Clean up model name
let modelName = bestResult.Name || 'Unknown Model';

if (modelName.includes('X1 Carbon')) {
  const carbonMatch = modelName.match(/(X1 Carbon \d+\w* Gen)/i);
  if (carbonMatch) modelName = carbonMatch[1];
} else if (modelName.includes('T490s')) {
  modelName = 'ThinkPad T490s';
} else if (modelName.includes('T490')) {
  modelName = 'ThinkPad T490';
} else if (modelName.includes('T480')) {
  modelName = 'ThinkPad T480s';
}

modelName = modelName
  .replace(/[-_]/g, ' ')
  .replace(/\s+/g, ' ')
  .replace(/\s*\(.*?\)\s*/g, ' ')
  .trim();

console.log(`Final: ${serialNumber} -> "${modelName}" (Item: ${itemId})`);

return {
  itemId,
  serialNumber,
  Name: modelName
};

