// Log input for debugging
const inputData = $input.first().json;
console.log("HTML Extract input fields:", Object.keys(inputData));

const html = inputData.data || inputData.html || inputData.body || inputData.content || inputData.response || "";
console.log("HTML length:", html.length, "Sample:", html.slice(0, 500));

if (!html) {
  console.log("No HTML content found");
  return { error: "No HTML content found", availableFields: Object.keys(inputData) };
}

const MAX_LENGTH = 200000;
let matches = null;
let text = "";

// --- Step 1: Extract from <p> and <h1-h6> ---
try {
  matches = html.match(/<(p|h[1-6])[^>]*>(.*?)<\/(p|h[1-6])>/gis);
  if (matches) {
    text = matches
      .map(m => m.replace(/<[^>]+>/g, "").trim())
      .filter(t => t.length > 5)
      .join("\n\n");
  }
} catch (e) {
  console.log("Regex error (p/h-tags):", e.message);
}

// --- Step 2: Fallback to <div>/<span> with article-related classes ---
if (text.length < 100) {
  console.log("Fallback: checking <div>/<span> for article text");
  try {
    matches = html.match(/<(div|span)[^>]*(article|content|body|text)[^>]*>(.*?)<\/(div|span)>/gis);
    if (matches) {
      text = matches
        .map(m => m.replace(/<[^>]+>/g, "").trim())
        .filter(t => t.length > 5)
        .join("\n\n");
    }
  } catch (e) {
    console.log("Regex error (div/span fallback):", e.message);
  }
}

// --- Step 3: Fallback to embedded JSON (with recursive search) ---
function findArticleText(obj) {
  if (!obj || typeof obj !== "object") return null;

  if (obj.articleBody && typeof obj.articleBody === "string") return obj.articleBody;
  if (obj.text && typeof obj.text === "string" && obj.text.length > 100) return obj.text;

  if (Array.isArray(obj)) {
    for (const el of obj) {
      const result = findArticleText(el);
      if (result) return result;
    }
  } else {
    for (const key of Object.keys(obj)) {
      const result = findArticleText(obj[key]);
      if (result) return result;
    }
  }
  return null;
}

if (text.length < 100) {
  console.log("Fallback: checking for JSON article data");
  try {
    const jsonBlocks = html.match(/<script[^>]+type=["']application\/(ld\+json|json)["'][^>]*>([\s\S]*?)<\/script>/gi);
    if (jsonBlocks) {
      for (let block of jsonBlocks) {
        try {
          const clean = block.replace(/<script[^>]*>/i, "").replace(/<\/script>/i, "").trim();
          const parsed = JSON.parse(clean);
          const result = findArticleText(parsed);
          if (result) {
            text = result;
            break;
          }
        } catch (err) {
          console.log("JSON parse error in block:", err.message);
        }
      }
    }
  } catch (e) {
    console.log("JSON parse fallback error:", e.message);
  }
}

// --- Step 4: Final error handling ---
if (!text || text.length < 100) {
  console.log("Article body not found (likely JS-rendered or blocked).");
  return { 
    error: "Article body not found (likely JS-rendered or blocked)", 
    availableFields: Object.keys(inputData),
    originalLength: html.length,
    rawSample: html.slice(0, 500)
  };
}

console.log("Extracted text length:", text.length, "Sample:", text.slice(0, 500));

if (text.length > MAX_LENGTH) {
  text = text.substring(0, MAX_LENGTH) + "... [truncated]";
}

return { 
  article: text, 
  originalLength: html.length, 
  extractedLength: text.length 
};

// Log input
console.log("Validator input:", JSON.stringify($json, null, 2));

let raw = $json.output || $json.text || $json.content || "";
if (typeof raw !== "string") raw = JSON.stringify(raw);

// Normalize
let s = raw
  .replace(/-/g, "-")
  .replace(/[\r\n]+/g, " ")
  .replace(/[\u2018\u2019]/g, "'")
  .replace(/[\u201C\u201D]/g, '"')
  .trim();

// Remove markdown
s = s.replace(/```json\s*/gi, "").replace(/```\s*/g, "").trim();

let obj = null;
try {
  obj = JSON.parse(s);
  if (typeof obj === "string") obj = JSON.parse(obj);
} catch (e) {
  console.log("JSON parse failed:", e.message);
}

// Try regex salvage if parse failed
if (!obj || typeof obj !== "object") {
  const titleMatch = s.match(/"title"\s*:\s*"([^"]+)"/);
  const bulletsMatch = s.match(/"bullets"\s*:\s*\[([\s\S]*?)\]/);

  if (titleMatch && bulletsMatch) {
    const title = titleMatch[1];
    const bullets = [...bulletsMatch[1].matchAll(/"([^"]+)"/g)].map(m => m[1]);

    return [{
      json: {
        title,
        bullets,
        url: $json.url || null,
        source: $json.source || "Local",
        language: $json.language || "en"
      }
    }];
  }

  return [{
    json: {
      error: "Failed to parse AI output into JSON",
      raw: s.slice(0, 500),
      url: $json.url || null,
      language: $json.language || "en"
    }
  }];
}

// Normal success path
let title = obj.title || "Summary";
let bullets = Array.isArray(obj.bullets) ? obj.bullets : [];

if (bullets.length > 5) bullets = bullets.slice(0, 5);
while (bullets.length < 5) {
  bullets.push("No further details available.");
}

return [{
  json: {
    title,
    bullets,
    url: $json.url || null,
    source: $json.source || "Local",
    language: $json.language || "en"
  }
}];

const { 
  title = "Summary", 
  bullets = [], 
  error = null, 
  raw = "", 
  url = "",
  language = "en"
} = $json || {};

// --- Error case ---
if (error) {
  const errorMsg = language === "he" ? "שגיאה בעיבוד המאמר" : "Error processing article";
  const sourceText = language === "he" ? "מקור:" : "Source:";

  return [{
    json: {
      text: `⚠️ ${errorMsg}\n\n${sourceText} ${url ? `<${url}|${url}>` : (language === "he" ? "לא ידוע" : "Unknown")}\n\n\`\`\`\n${raw?.slice(0, 500) || (language === "he" ? "אין תוכן" : "No content")}\n\`\`\``
    }
  }];
}

// Clean title
let cleanTitle = String(title)
  .replace(/^[^{"]*"?title["']?\s*[:=–-]\s*/i, "")
  .replace(/^["']|["']$/g, "")
  .replace(/[.,"]+$/, "")
  .trim()
  .replace(/automated with this n8n workflow/i, "")
  .trim();

// Clean bullets
let cleanBullets = (Array.isArray(bullets) ? bullets : [])
  .map(b => String(b).trim())
  .filter(b => b && !/^["']?title["']?\s*[:=–-]/i.test(b))
  .filter(b => !/automated with this n8n workflow/i.test(b))
  .map(b => {
    let cleaned = b.replace(/^["']|["']$/g, "").replace(/[.,"]+$/, "").trim();

    // Strip ALL bullet characters
    cleaned = cleaned.replace(/^[•\s]+/g, "").trim();

    // Fix double periods
    cleaned = cleaned.replace(/\.{2,}/g, ".");

    // CAPITALIZE FIRST LETTER
    if (cleaned.length > 0) {
      cleaned = cleaned.charAt(0).toUpperCase() + cleaned.slice(1);
    }

    // Add period if needed
    const needsPeriod = !/[.!?]$/.test(cleaned) && !/[׃׀]$/.test(cleaned);
    return needsPeriod ? `${cleaned}.` : cleaned;
  })
  .filter(Boolean)
  .slice(0, 5);

// Guarantee 5 bullets
const defaultBullet = language === "he" ? "לא נמצאו פרטים נוספים" : "No further details available";
while (cleanBullets.length < 5) {
  cleanBullets.push(defaultBullet);
}

// Build Slack message
const RTL = "\u200F";
const summaryText = language === "he" ? "סיכום:" : "Summary:";

if (language === "he") {
  const parts = [
    `:brain: ${summaryText} ${url ? `<${url}|${RTL}${cleanTitle}${RTL}>` : RTL + cleanTitle + RTL}`,
    "",
    ...cleanBullets.map((b, i) => `${i + 1}. ${RTL}${b}${RTL}`),
    ""
  ];
  return [{ json: { text: parts.join("\n") } }];
} else {
  const parts = [
    `:brain: ${summaryText} ${url ? `<${url}|${cleanTitle}>` : cleanTitle}`,
    "",
    ...cleanBullets.map(b => `• ${b}`),
    ""
  ];
  return [{ json: { text: parts.join("\n") } }];
}

console.log("Chunker input article length:", $json.article?.length || 0, "Sample:", $json.article?.slice(0, 500));

const CHUNK_SIZE = 2000;
const OVERLAP = 150;
const MAX_CHUNKS = 20;

const text = $json.article || "";
const url = $json.url || "";
const chunks = [];

for (let i = 0; i < text.length && chunks.length < MAX_CHUNKS; i += (CHUNK_SIZE - OVERLAP)) {
  chunks.push({ json: { url, chunk: text.slice(i, i + CHUNK_SIZE), index: chunks.length + 1 } });
}

console.log("Generated chunks:", chunks.length, "Sample chunk:", chunks[0]?.json.chunk.slice(0, 500));
return chunks;

// LANGUAGE DETECTOR NODE
const text = $json.article || $json.chunk || "";
if (!text) {
  return [{
    json: { 
      language: "en",
      chunk: $json.chunk || "", 
      url: $json.url || "" 
    }
  }];
}

// Count Hebrew characters
const hebrewMatches = text.match(/[\u0590-\u05FF]/g) || [];
const hebrewChars = hebrewMatches.length;
const totalChars = text.length;
const hebrewRatio = hebrewChars / totalChars;

// Only treat as Hebrew if at least 20% of characters are Hebrew AND total length > 200
const language = (hebrewRatio > 0.2 && totalChars > 200) ? "he" : "en";

console.log(`Language detected: ${language}, Hebrew chars: ${hebrewChars}, Ratio: ${hebrewRatio.toFixed(3)}, Total: ${totalChars}`);

return [{
  json: {
    language,
    chunk: $json.chunk || "",
    url: $json.url || ""
  }
}];
