const domain = 'alex-it.net';
const zoneID = '3462e9f3ae9f12d67018a49274db3def';

// Get all inputs and log them
const allInputs = $input.all();
console.log('=== DEBUG: All inputs received ===');
console.log('Number of inputs:', allInputs.length);

allInputs.forEach((input, index) => {
  console.log(`Input ${index}:`, JSON.stringify(input.json, null, 2).substring(0, 500));
});

// Find current IP
const ipInput = allInputs.find(item => item.json && item.json.ip);
const currentIP = ipInput ? ipInput.json.ip : null;
console.log('Current IP found:', currentIP);

// Find DNS records - with detailed checking
const dnsInput = allInputs.find(item => {
  const hasJson = item.json != null;
  const hasResult = item.json && item.json.result != null;
  const isArray = item.json && Array.isArray(item.json.result);
  console.log('Checking input:', { hasJson, hasResult, isArray });
  return hasJson && hasResult && isArray;
});

console.log('DNS input found:', !!dnsInput);

if (dnsInput) {
  console.log('DNS response structure:', {
    hasSuccess: 'success' in dnsInput.json,
    successValue: dnsInput.json.success,
    hasResult: 'result' in dnsInput.json,
    resultIsArray: Array.isArray(dnsInput.json.result),
    resultLength: dnsInput.json.result ? dnsInput.json.result.length : 0
  });
}

const dnsResponse = dnsInput ? dnsInput.json : null;

if (!currentIP) {
  throw new Error('Could not find current IP in inputs');
}

if (!dnsResponse) {
  throw new Error('Could not find DNS records input');
}

if (!dnsResponse.result) {
  throw new Error('DNS response has no result field');
}

if (!Array.isArray(dnsResponse.result)) {
  throw new Error('DNS response result is not an array');
}

// Extract ALL A records that are NOT proxied
const sites = dnsResponse.result
  .filter(record => {
    if (record.type !== 'A') return false;

    if (record.proxied === true) {
      console.log(`Skipping proxied record: ${record.name}`);
      return false;
    }

    return record.name.endsWith(`.${domain}`) || record.name === domain;
  })
  .map(record => {
    const subdomain = record.name === domain ? '@' : record.name.replace(`.${domain}`, '');
    return { 
      subdomain,
      currentIP: record.content
    };
  });

console.log(`Found ${sites.length} non-proxied A records to manage:`);
sites.forEach(s => console.log(`  - ${s.subdomain} (current: ${s.currentIP})`));

return [{
  json: {
    domain,
    zoneID,
    newIP: currentIP,
    sites: sites.map(s => ({ 
      subdomain: s.subdomain,
      fqdn: s.subdomain === '@' ? domain : `${s.subdomain}.${domain}`
    }))
  }
}];

// Fan out one item per FQDN, attach zoneID
const original = $input.first().json;

if (!original || !Array.isArray(original.sites)) throw new Error('sites missing or not an array');

return original.sites.map(site => ({
  json: {
    subdomain: site.subdomain,
    fqdn: site.fqdn,
    domain: original.domain,
    newIP: original.newIP,
    zoneID: original.zoneID
  }
}));

// Merge Cloudflare response with original site data using paired items
const cfItems = $input.all();
return cfItems.map((item, i) => {
  const pairedIndex = (item.pairedItem && item.pairedItem.item != null) ? item.pairedItem.item : i;
  const original = $items('Expand Sites')[pairedIndex].json;
  const record = item.json?.result?.[0] || {};

  return {
    json: {
      subdomain: original.subdomain,
      fqdn: original.fqdn,
      domain: original.domain,
      newIP: original.newIP,
      zoneID: original.zoneID,
      recordId: record.id || '',
      existingIP: record.content || '',
      existingTTL: record.ttl != null ? record.ttl : 1,
      existingProxied: record.proxied != null ? record.proxied : false
    }
  };
});

// Evaluate each incoming item (one per FQDN)
const items = $input.all();

const desiredTTL = 1;         // Cloudflare "Auto"
const desiredProxied = false; // DNS only

return items.map(item => {
  const j = item.json;
  const hasRecord = !!j.recordId;

  const needsUpdate =
    !hasRecord ||
    j.existingIP !== j.newIP ||
    j.existingTTL !== desiredTTL ||
    j.existingProxied !== desiredProxied;

  console.log(
    `[${j.fqdn}] ${j.existingIP || '<none>'} -> ${j.newIP} | ttl:${j.existingTTL} proxied:${j.existingProxied} | update? ${needsUpdate}`
  );

  return {
    json: { ...j, hasRecord, needsUpdate, desiredTTL, desiredProxied }
  };
});

return $input.all().map(item => {
  const res = item.json || {};
  const fqdn = (res.result && res.result.name) || res.name || 'unknown';
  const success = res.success === true;
  const error = success ? undefined : ((res.errors && res.errors[0] && res.errors[0].message) || res.message || 'Unknown error');
  return { json: { success, action: 'updated', fqdn, error } };
});

return $input.all().map(item => {
  const res = item.json || {};
  const fqdn = (res.result && res.result.name) || res.name || 'unknown';
  const success = res.success === true;
  const error = success ? undefined : ((res.errors && res.errors[0] && res.errors[0].message) || res.message || 'Unknown error');
  return { json: { success, action: 'created', fqdn, error } };
});

const results = $input.all();
const timestamp = new Date().toISOString();
let created = 0, updated = 0, noChange = 0, failed = 0;

const createdFQDNs = [];
const updatedFQDNs = [];

for (const item of results) {
  const r = item.json;
  if (!r || r.success === false) {
    failed++;
    continue;
  }

  if (r.action === 'created') {
    created++;
    createdFQDNs.push(r.fqdn);
  } else if (r.action === 'updated') {
    updated++;
    updatedFQDNs.push(r.fqdn);
  } else if (r.action === 'no_change') {
    noChange++;
  }
}

console.log(`DNS Summary ${timestamp} -> created: ${created}, updated: ${updated}, no change: ${noChange}, failed: ${failed}`);

return [{
  json: {
    timestamp,
    created,
    updated,
    noChange,
    failed,
    total: results.length,
    createdFQDNs,
    updatedFQDNs
  }
}];

