const updatedText = ($json["updatedFQDNs"] && $json["updatedFQDNs"].length > 0)
  ? "\n\n:repeat: Updated domains with new IPs:\n" +
    $json["updatedFQDNs"]
      .map(fqdn => {
        const match = $items('Prepare Payload').find(i => i.json.fqdn === fqdn);
        const newIP = match?.json?.newIP || 'unknown';
        const oldIP = match?.json?.existingIP || 'unknown';
        return `${fqdn} updated to ${newIP} from old IP ${oldIP}`;
      }).join("\n")
  : "";

// ðŸ†• Created domains
const createdText = ($json["createdFQDNs"] && $json["createdFQDNs"].length > 0)
  ? "\n\n:new: Created domains:\n" + $json["createdFQDNs"].join("\n")
  : "";

// âœ… No change message
if ($json["created"] === 0 && $json["updated"] === 0 && $json["failed"] === 0) {
  return [{
    json: {
      message: `âœ… DNS check completed at ${timestamp} â€” no changes detected.`
    }
  }];
}

// ðŸ“© Final formatted Slack message
return [{
  json: {
    message: summary + updatedText + createdText
  }
}];
