// Process each input item
return items.map((item, index) => {
  const body = item.json.body || item.json;
  const event = body.event || {};
  const headers = item.json.headers || {};

  console.log('Received event type:', body.type);
  console.log('Event details:', JSON.stringify(event, null, 2));

  // 1) Handle URL verification challenge
  if (body.type === 'url_verification') {
    console.log('URL verification challenge');
    return {
      json: { challenge: body.challenge },
      pairedItem: { item: index }
    };
  }

  // 2) Skip Slack retries
  if (headers['x-slack-retry-num'] || headers['x-slack-retry-reason']) {
    console.log('Skipping Slack retry');
    return null;
  }

  // 3) Skip if not a message event or if it's a bot message
  if (event.type !== 'message' || event.subtype === 'bot_message' || event.bot_id) {
    console.log('Not a user message, skipping');
    return null;
  }

  // 4) Skip messages without text
  if (!event.text) {
    console.log('No text in message');
    return null;
  }

  // 5) Skip messages from your bot
  const BOT_USER_ID = 'U07K8QJ9XYZ';
  if (BOT_USER_ID && event.user === BOT_USER_ID) {
    console.log('Ignoring message from bot');
    return null;
  }

  // 6) IMPROVED URL EXTRACTION - Works with mobile
  const text = event.text || "";
  const urls = new Set();

  console.log('Raw message text:', text);

  // Decode URL-encoded text (mobile often sends encoded URLs)
  let decodedText = text;
  try {
    decodedText = decodeURIComponent(text);
  } catch (e) {
    decodedText = text;
  }

  // Pattern 1: Slack format <http://example.com|label> or <http://example.com>
  const slackUrlRegex = /<(https?:\/\/[^>|]+)(\|[^>]+)?>/g;
  let match;
  while ((match = slackUrlRegex.exec(decodedText)) !== null) {
    urls.add(match[1]);
  }

  // Pattern 2: URL-encoded Slack format (mobile)
  const encodedSlackRegex = /%3C(https?:\/\/[^%>|]+)(%7C[^%>]+)?%3E/gi;
  while ((match = encodedSlackRegex.exec(text)) !== null) {
    try {
      urls.add(decodeURIComponent(match[1]));
    } catch (e) {
      urls.add(match[1]);
    }
  }

  // Pattern 3: Plain URLs
  const plainUrlRegex = /https?:\/\/[^\s<>"'()[\]{}]+/g;
  while ((match = plainUrlRegex.exec(decodedText)) !== null) {
    urls.add(match[0]);
  }

  // Pattern 4: Fully encoded URLs
  const encodedUrlRegex = /https?%3A%2F%2F[^\s<>"'()[\]{}]+/gi;
  while ((match = encodedUrlRegex.exec(text)) !== null) {
    try {
      urls.add(decodeURIComponent(match[0]));
    } catch (e) {
      urls.add(match[0]);
    }
  }

  console.log('All URLs found:', Array.from(urls));

  // Clean and validate
  const cleanUrls = new Set();
  for (const url of urls) {
    let cleanUrl = url.trim().replace(/[.,;:)]+$/, '');
    if (cleanUrl.match(/^https?:\/\/.+\..+/)) {
      cleanUrls.add(cleanUrl);
    }
  }

  const firstUrl = Array.from(cleanUrls)[0] || '';

  if (!firstUrl) {
    console.log('No valid URL found in message:', text);
    return null;
  }

  console.log('Selected URL:', firstUrl);

  // 7) DEDUPLICATION - Check URL instead of event_id
  const staticData = $getWorkflowStaticData('global');

  if (!staticData.processedUrls) {
    staticData.processedUrls = {};
  }

  const hourAgo = Date.now() - 3600000; // 1 hour

  // Check if this URL was processed recently
if (staticData.processedUrls[firstUrl] && staticData.processedUrls[firstUrl] > hourAgo) {
  console.log('URL already processed recently, sending duplicate notice');
  return {
    json: {
      isDuplicate: true,
      text: `ℹ️ This URL was already summarized recently.\n\n<${firstUrl}|${firstUrl}>`
    },
    pairedItem: { item: index }
  };
}

  // Mark URL as processed
  staticData.processedUrls[firstUrl] = Date.now();

  // Clean old entries (older than 24 hours)
  const dayAgo = Date.now() - 86400000;
  Object.keys(staticData.processedUrls).forEach(url => {
    if (staticData.processedUrls[url] < dayAgo) {
      delete staticData.processedUrls[url];
    }
  });

  return {
    json: {
      url: firstUrl,
      text: text,
      slack_channel: event.channel,
      slack_user: event.user,
      slack_ts: event.ts,
      slack_thread_ts: event.thread_ts,
      event_id: body.event_id || `${event.ts}-${Date.now()}`
    },
    pairedItem: { item: index }
  };
}).filter(item => item !== null);

// Enhanced HTML Extract - Better for news sites
const inputData = $input.first().json;
console.log("HTML Extract input fields:", Object.keys(inputData));

const html = inputData.data || inputData.html || inputData.body || inputData.content || inputData.response || "";
const url = inputData.url || "";

console.log("URL:", url);
console.log("HTML length:", html.length, "Sample:", html.slice(0, 500));

if (!html || html.length < 100) {
  console.log("No HTML content found");
  return [{ 
    json: {
      error: "No HTML content received from website",
      url: url
    }
  }];
}

const MAX_LENGTH = 200000;
let text = "";

// --- YNET SPECIFIC: Try structured data first ---
console.log("Trying JSON-LD structured data");
try {
  const jsonLdMatch = html.match(/<script[^>]*type=["']application\/ld\+json["'][^>]*>([\s\S]*?)<\/script>/i);
  if (jsonLdMatch) {
    const jsonData = JSON.parse(jsonLdMatch[1]);
    if (jsonData.articleBody) {
      text = jsonData.articleBody;
      console.log("✅ Found content in JSON-LD");
    }
  }
} catch (e) {
  console.log("JSON-LD extraction failed:", e.message);
}

// --- Try meta description for headline context ---
let metaDescription = "";
try {
  const metaMatch = html.match(/<meta[^>]*name=["']description["'][^>]*content=["']([^"']+)["']/i) ||
                    html.match(/<meta[^>]*content=["']([^"']+)["'][^>]*name=["']description["']/i);
  if (metaMatch) {
    metaDescription = metaMatch[1];
  }
} catch (e) {}

// --- Step 1: Try <article> tag ---
if (text.length < 500) {
  console.log("Trying <article> tag");
  try {
    const articleMatch = html.match(/<article[^>]*>([\s\S]*?)<\/article>/i);
    if (articleMatch) {
      text = articleMatch[1]
        .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, "")
        .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "")
        .replace(/<nav[^>]*>[\s\S]*?<\/nav>/gi, "")
        .replace(/<aside[^>]*>[\s\S]*?<\/aside>/gi, "")
        .replace(/<header[^>]*>[\s\S]*?<\/header>/gi, "")
        .replace(/<footer[^>]*>[\s\S]*?<\/footer>/gi, "")
        .replace(/<div[^>]*class="[^"]*comment[^"]*"[^>]*>[\s\S]*?<\/div>/gi, "")
        .replace(/<div[^>]*class="[^"]*ad[^"]*"[^>]*>[\s\S]*?<\/div>/gi, "")
        .replace(/<[^>]+>/g, " ")
        .replace(/\s+/g, " ")
        .trim();

      if (text.length > 500) {
        console.log("✅ Found content in <article>");
      } else {
        text = "";
      }
    }
  } catch (e) {
    console.log("Article tag error:", e.message);
  }
}

// --- Step 2: Try <main> tag ---
if (text.length < 500) {
  console.log("Trying <main> tag");
  const mainMatch = html.match(/<main[^>]*>([\s\S]*?)<\/main>/i);
  if (mainMatch) {
    const extracted = mainMatch[1]
      .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, "")
      .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "")
      .replace(/<nav[^>]*>[\s\S]*?<\/nav>/gi, "")
      .replace(/<aside[^>]*>[\s\S]*?<\/aside>/gi, "")
      .replace(/<div[^>]*class="[^"]*ad[^"]*"[^>]*>[\s\S]*?<\/div>/gi, "")
      .replace(/<[^>]+>/g, " ")
      .replace(/\s+/g, " ")
      .trim();

    if (extracted.length > text.length) {
      text = extracted;
      console.log("✅ Found content in <main>");
    }
  }
}

// --- Step 3: Try common Israeli news site patterns ---
if (text.length < 500) {
  console.log("Trying Israeli news site patterns");
  const contentPatterns = [
    // Ynet specific
    /<div[^>]*class="[^"]*art_body[^"]*"[^>]*>([\s\S]*?)<\/div>/i,
    /<div[^>]*class="[^"]*article_text[^"]*"[^>]*>([\s\S]*?)<\/div>/i,
    /<div[^>]*class="[^"]*text_block[^"]*"[^>]*>([\s\S]*?)<\/div>/i,
    // Generic patterns
    /<div[^>]*class="[^"]*(?:article-body|post-content|entry-content|main-content|article-content|article__content|post-body|content-body|story-body)[^"]*"[^>]*>([\s\S]*?)<\/div>/i,
    /<div[^>]*id="[^"]*(?:article|content|main|post|story)[^"]*"[^>]*>([\s\S]*?)<\/div>/i
  ];

  for (const pattern of contentPatterns) {
    const match = html.match(pattern);
    if (match) {
      const extracted = match[1]
        .replace(/<script[^>]*>[\s\S]*?<\/script>/gi, "")
        .replace(/<style[^>]*>[\s\S]*?<\/style>/gi, "")
        .replace(/<nav[^>]*>[\s\S]*?<\/nav>/gi, "")
        .replace(/<aside[^>]*>[\s\S]*?<\/aside>/gi, "")
        .replace(/<div[^>]*class="[^"]*(?:ad|banner|social|share|comment)[^"]*"[^>]*>[\s\S]*?<\/div>/gi, "")
        .replace(/<[^>]+>/g, " ")
        .replace(/\s+/g, " ")
        .trim();

      if (extracted.length > text.length && extracted.length > 500) {
        text = extracted;
        console.log("✅ Found content in news site pattern");
        break;
      }
    }
  }
}

// --- Step 4: Aggressive paragraph extraction ---
if (text.length < 500) {
  console.log("Trying aggressive paragraph extraction");
  try {
    // Get all paragraphs
    const matches = html.match(/<p[^>]*>(.*?)<\/p>/gis);
    if (matches && matches.length > 5) {
      const paragraphs = matches
        .map(m => m.replace(/<[^>]+>/g, "").trim())
        .filter(t => {
          const lowerText = t.toLowerCase();
          return t.length > 50 && 
                 !lowerText.includes('cookie') &&
                 !lowerText.includes('פרטיות') &&
                 !lowerText.includes('privacy') &&
                 !lowerText.includes('תנאי שימוש') &&
                 !lowerText.includes('הרשמה') &&
                 !lowerText.includes('subscribe') &&
                 !lowerText.includes('newsletter');
        });

      if (paragraphs.length >= 5) {
        // Add meta description as headline if available
        text = (metaDescription ? metaDescription + "\n\n" : "") + paragraphs.join("\n\n");
        console.log("✅ Found content in paragraphs");
      }
    }
  } catch (e) {
    console.log("Paragraph extraction error:", e.message);
  }
}

// --- Step 5: Check for JavaScript-rendered content indicators ---
if (text.length < 500) {
  console.log("⚠️ Checking for JS-heavy site indicators");

  const jsIndicators = [
    'window.__INITIAL_STATE__',
    'window.__PRELOADED_STATE__',
    'data-react-root',
    'id="__next"',
    'id="root"',
    /__NUXT__/,
    /window\.__APOLLO_STATE__/
  ];

  const hasJsIndicators = jsIndicators.some(indicator => {
    if (typeof indicator === 'string') {
      return html.includes(indicator);
    }
    return indicator.test(html);
  });

  if (hasJsIndicators) {
    return [{ 
      json: {
        error: "This site uses heavy JavaScript rendering. Content cannot be extracted without a browser.",
        url: url,
        suggestion: "Try sharing a direct link to the article text or PDF version"
      }
    }];
  }
}

// --- Step 6: Clean extracted text ---
if (text.length > 0) {
  text = text
    // Remove author bios
    .replace(/is a (writer|journalist|author|reporter|editor).*?(\.|$)/gi, "")
    .replace(/writes? (about|for|on).*?(\.|$)/gi, "")
    .replace(/based in.*?(\.|$)/gi, "")
    .replace(/כתב(?:ת)? ב.*?(\.|$)/gi, "")
    // Remove common footer/header text
    .replace(/all rights reserved/gi, "")
    .replace(/כל הזכויות שמורות/gi, "")
    .replace(/copyright \d{4}/gi, "")
    .replace(/©\s*\d{4}/gi, "")
    // Remove social media prompts
    .replace(/follow us on.*/gi, "")
    .replace(/עקבו אחרינו.*/gi, "")
    // Clean whitespace
    .replace(/\s+/g, " ")
    .trim();
}

// --- Final validation ---
if (!text || text.length < 300) {
  console.log("❌ Article body not found or too short");

  // Provide specific error message
  let errorDetail = "Could not extract article text. ";

  if (html.includes('נדרשת הרשמה') || html.includes('subscription required')) {
    errorDetail += "Site requires subscription or login.";
  } else if (html.length < 5000) {
    errorDetail += "Page content too small - might be blocked or redirected.";
  } else {
    errorDetail += "Site may use JavaScript rendering or paywall.";
  }

  return [{ 
    json: {
      error: errorDetail,
      url: url,
      htmlLength: html.length,
      extractedLength: text.length
    }
  }];
}

console.log(`✅ Successfully extracted ${text.length} characters`);
console.log("Sample:", text.slice(0, 300));

if (text.length > MAX_LENGTH) {
  text = text.substring(0, MAX_LENGTH) + "... [truncated]";
}

return [{ 
  json: {
    article: text, 
    url: url,
    originalLength: html.length, 
    extractedLength: text.length 
  }
}];

// === HELPER FUNCTION - AT TOP ===
function cleanBullets(bullets, language = "en") {
  return bullets
    .map(b => {
      let cleaned = String(b).trim();

      // Remove workflow text
      cleaned = cleaned.replace(/\*Automated with this n8n workflow\*/gi, "");
      cleaned = cleaned.replace(/Automated with this n8n workflow/gi, "");

      // Remove ALL bullets/dashes from start
      cleaned = cleaned.replace(/^[•\-\s]+/g, "").trim();

      // Fix multiple periods
      cleaned = cleaned.replace(/\.{2,}/g, ".");

      // Fix period-bullet patterns
      cleaned = cleaned.replace(/\.\s*•+\s*/g, ". ");

      // Remove duplicate spaces
      cleaned = cleaned.replace(/\s{2,}/g, " ");

      // Remove quotes
      cleaned = cleaned.replace(/^["']|["']$/g, "");

      return cleaned.trim();
    })
    .filter(b => b && b.length > 20); // Filter short/empty bullets
}

// Log input
console.log("Validator input:", JSON.stringify($json, null, 2));

let raw = $json.output || $json.text || $json.content || "";
if (typeof raw !== "string") raw = JSON.stringify(raw);

// Get language from input
const language = $json.language || "en";
console.log("Language in Validator:", language);

// Aggressive cleaning
let s = raw
  .replace(/```json\s*/gi, "")
  .replace(/```\s*/g, "")
  .replace(/\*Automated with.*$/gim, "")
  .replace(/^[^{]*/, "")  // Remove before {
  .replace(/[^}]*$/, "")  // Remove after }
  .replace(/-/g, "-")
  .replace(/[\r\n]+/g, " ")
  .replace(/[\u2018\u2019]/g, "'")
  .replace(/[\u201C\u201D]/g, '"')
  .replace(/\s+/g, " ")
  .trim();

console.log("Cleaned string:", s.slice(0, 300));

let obj = null;
try {
  obj = JSON.parse(s);
  if (typeof obj === "string") obj = JSON.parse(obj);
} catch (e) {
  console.log("JSON parse failed:", e.message);
}

// Try regex salvage if parse failed
if (!obj || typeof obj !== "object") {
  const titleMatch = s.match(/"title"\s*:\s*"([^"]+)"/);
  const bulletsMatch = s.match(/"bullets"\s*:\s*\[([\s\S]*?)\]/);

  if (titleMatch && bulletsMatch) {
    const title = titleMatch[1];
    const bullets = [...bulletsMatch[1].matchAll(/"([^"]+)"/g)]
      .map(m => m[1])
      .filter(b => b.length > 10);

    return [{
      json: {
        title,
        bullets: cleanBullets(bullets, language),
        url: $json.url || null,
        language: language,  // ✅ PASS LANGUAGE
        source: $json.source || "Local"
      }
    }];
  }

  return [{
    json: {
      error: "Failed to parse AI output into JSON",
      raw: s.slice(0, 500),
      url: $json.url || null,
      language: language  // ✅ PASS LANGUAGE
    }
  }];
}

// Normal success path
let title = obj.title || "Summary";
let bullets = Array.isArray(obj.bullets) ? obj.bullets : [];

// Clean bullets
bullets = cleanBullets(bullets, language);

// Ensure exactly 5 bullets
if (bullets.length > 5) bullets = bullets.slice(0, 5);

const defaultBullet = language === "he" 
  ? "לא נמצאו פרטים נוספים." 
  : "No further details available.";

while (bullets.length < 5) {
  bullets.push(defaultBullet);
}

return [{
  json: {
    title,
    bullets,
    url: $json.url || null,
    language: language,  // ✅ PASS LANGUAGE
    source: $json.source || "Local"
  }
}];

const { 
  title = "Summary", 
  bullets = [], 
  error = null, 
  raw = "", 
  url = "",
  language = "en"
} = $json || {};

// --- Error case ---
if (error) {
  const errorMsg = language === "he" ? "שגיאה בעיבוד המאמר" : "Error processing article";
  const sourceText = language === "he" ? "מקור:" : "Source:";

  return [{
    json: {
      text: `⚠️ ${errorMsg}\n\n${sourceText} ${url ? `<${url}|${url}>` : (language === "he" ? "לא ידוע" : "Unknown")}`
    }
  }];
}

// --- Clean title ---
let cleanTitle = String(title)
  .replace(/^[^{"]*"?title["']?\s*[:=–-]\s*/i, "")
  .replace(/^["']|["']$/g, "")
  .replace(/[.,"]+$/, "")
  .replace(/automated with this n8n workflow/i, "")
  .replace(/\*Automated with.*$/i, "")
  .replace(/^תקציר[:\s]+/i, "")
  .replace(/^סיכום[:\s]+/i, "")
  .replace(/^summary[:\s]+/i, "")
  .trim();

// --- Clean bullets ---
let cleanBullets = (Array.isArray(bullets) ? bullets : [])
  .map(b => String(b).trim())
  .filter(b => {
    if (language === "he" && (
      b === "No further details available." || 
      b === "No further details available"
    )) {
      return false;
    }
    return b && b.length > 25;
  })
  .map(b => {
    let cleaned = b
      .replace(/^["']|["']$/g, "")
      .replace(/automated with this n8n workflow/i, "")
      .replace(/\*Automated with.*$/i, "")
      .replace(/^[•\-\s]+/, "")
      .replace(/\.{2,}/g, ".")
      .replace(/\s+/g, " ")
      .trim();

    return cleaned;
  })
  .filter(Boolean)
  .slice(0, 5);

const defaultBullet = language === "he" 
  ? "לא נמצאו פרטים נוספים בנושא זה" 
  : "No further details available";

while (cleanBullets.length < 5) {
  cleanBullets.push(defaultBullet);
}

// --- Track metrics ---
const metrics = $getWorkflowStaticData('global').metrics || { total: 0, success: 0 };
metrics.total++;
metrics.success++;
$getWorkflowStaticData('global').metrics = metrics;
console.log(`Success rate: ${(metrics.success/metrics.total*100).toFixed(1)}%`);

// --- Build Slack message ---
const RLM = "\u200F"; // Right-to-Left Mark
const LRM = "\u200E"; // Left-to-Right Mark

if (language === "he") {
  // Improved regex to wrap English, numbers, and special characters
  function wrapEnglish(text) {
    // Match English words, numbers with decimals/commas, and mixed content
    return text.replace(/([A-Za-z0-9]+(?:[.,:/\-][A-Za-z0-9]+)*)/g, `${LRM}$1${LRM}`);
  }

  const wrappedTitle = wrapEnglish(cleanTitle);

  // Hebrew: NO "סיכום:" prefix, just emoji + title
  const titleLine = url 
    ? `${RLM}:brain: <${url}|${wrappedTitle}>` 
    : `${RLM}:brain: ${wrappedTitle}`;

  const bulletLines = cleanBullets.map(b => {
    const wrappedBullet = wrapEnglish(b);
    return `${RLM}• ${wrappedBullet}`;
  });

  return [{ 
    json: { 
      text: [titleLine, "", ...bulletLines, ""].join("\n")
    } 
  }];

} else {
  // English: Keep "Summary:" prefix
  const titleLine = url 
    ? `:brain: Summary: <${url}|${cleanTitle}>` 
    : `:brain: Summary: ${cleanTitle}`;

  const bulletLines = cleanBullets.map(b => `• ${b}`);

  return [{ 
    json: { 
      text: [titleLine, "", ...bulletLines, ""].join("\n")
    } 
  }];
}

console.log("Chunker input article length:", $json.article?.length || 0, "Sample:", $json.article?.slice(0, 500));

const CHUNK_SIZE = 5000;   // smaller than before
const OVERLAP = 150;
const MAX_CHUNKS = 10;

const text = $json.article || "";
const url = $json.url || "";
const chunks = [];

for (let i = 0; i < text.length && chunks.length < MAX_CHUNKS; i += (CHUNK_SIZE - OVERLAP)) {
  chunks.push({ json: { url, chunk: text.slice(i, i + CHUNK_SIZE), index: chunks.length + 1 } });
}

console.log("Generated chunks:", chunks.length, "Sample chunk:", chunks[0]?.json.chunk.slice(0, 500));
return chunks;

// LANGUAGE DETECTOR NODE
const text = $json.article || $json.chunk || "";
if (!text) {
  return [{
    json: { 
      language: "en",  // Default fallback
      chunk: $json.chunk || "", 
      url: $json.url || "" 
    }
  }];
}

// Count Hebrew characters
const hebrewMatches = text.match(/[\u0590-\u05FF]/g) || [];
const hebrewChars = hebrewMatches.length;

// Count Cyrillic characters (Russian)
const cyrillicMatches = text.match(/[\u0400-\u04FF]/g) || [];
const cyrillicChars = cyrillicMatches.length;

const totalChars = text.length;
const hebrewRatio = hebrewChars / totalChars;
const cyrillicRatio = cyrillicChars / totalChars;

// Detect language - treat Russian as English for summarization
let language = "en";
if (hebrewRatio > 0.2 && totalChars > 200) {
  language = "he";
} else if (cyrillicRatio > 0.2 && totalChars > 200) {
  language = "en";  // Russian articles will be summarized in English
}

console.log(`Language detected: ${language}, Hebrew: ${hebrewChars}, Cyrillic: ${cyrillicChars}, Total: ${totalChars}`);

return [{
  json: {
    language,
    chunk: $json.chunk || "",
    url: $json.url || ""
  }
}];

// Format Error Message - FIXED
const error = $json.error || "Unknown error";
const url = $json.url || $input.first()?.json?.url || 'Unknown URL';
const language = $json.language || 'en';

console.log("Error formatting - URL:", url, "Error:", error);

const errorMsg = language === 'he' 
  ? '⚠️ נכשל בסיכום המאמר'
  : '⚠️ Failed to summarize article';

const detailMsg = language === 'he'
  ? `סיבה: ${error}`
  : `Reason: ${error}`;

const sourceText = language === 'he' ? 'מקור:' : 'Source:';

return [{
  json: {
    text: `${errorMsg}\n${detailMsg}\n\n${sourceText} <${url}|${url}>`
  }
}];

// Pre-fetch URL Checker
const url = $json.url;

// Check if URL is from known problematic sites
const problematicSites = {
  'ynet.co.il': 'mobile',
  'mako.co.il': 'mobile',
  'walla.co.il': 'mobile'
};

let strategy = 'desktop';
try {
  const domain = new URL(url).hostname;
  for (const [site, strat] of Object.entries(problematicSites)) {
    if (domain.includes(site)) {
      strategy = strat;
      console.log(`Using ${strategy} strategy for ${domain}`);
      break;
    }
  }
} catch (e) {}

// For mobile strategy, try to convert URL to mobile version
let fetchUrl = url;
if (strategy === 'mobile' && url.includes('ynet.co.il')) {
  // Some sites have mobile versions that are easier to parse
  fetchUrl = url.replace('www.ynet', 'm.ynet');
}

return [{
  json: {
    url: url,
    fetchUrl: fetchUrl,
    strategy: strategy
  }
}];
