// ----- 1) Get the email body from this payload shape -----
function htmlToText(html) {
  if (!html) return '';
  // Normalize line breaks before stripping tags
  html = html
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(p|div|li|tr|h\d)>/gi, '\n')
    .replace(/&nbsp;/gi, ' ');
  // Strip tags
  const text = html.replace(/<[^>]+>/g, ' ');
  // Collapse whitespace
  return text.replace(/\r?\n/g, '\n').replace(/[ \t]+\n/g, '\n').replace(/[ \t]{2,}/g, ' ').trim();
}

let emailBody =
  ($json.text && String($json.text)) ||
  htmlToText($json.html) ||
  htmlToText($json.textAsHtml) ||
  ($json.snippet && String($json.snippet)) ||
  '';

// If body still empty, try base64-encoded Gmail payload (rare here but safe)
if (!emailBody && $json.payload && $json.payload.body && $json.payload.body.data) {
  try {
    const buff = Buffer.from($json.payload.body.data, 'base64');
    emailBody = buff.toString('utf8');
  } catch (_) {}
}

// Safety: normalize whitespace for robust regex capture
const flat = (emailBody || '').replace(/\r/g, '').replace(/[ \t]+/g, ' ').trim();

// ----- 2) Robust field capture (works for single-line or multiline) -----
const LABELS = [
  'First Name','Last Name','Employee Number','Job Title',
  'Department','Manager SAM','Manager','Office Phone','Phone',
  'Office Location','Location','Source Username'
];

function esc(s){ return s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'); }

function captureAfter(label, src) {
  const labelEsc = esc(label);
  const next = LABELS.filter(l => l !== label).map(esc);
  const lookahead = next.length ? `(?=\\s*(?:${next.join('|')})\\s*:|$)` : '$';
  // Accept either single-line or multiline values
  const re = new RegExp(`${labelEsc}\\s*:\\s*([\\s\\S]*?)\\s*${lookahead}`, 'i');
  const m = src.match(re);
  return m ? m[1].trim() : '';
}

const userData = {
  firstname:      captureAfter('First Name', flat),
  lastname:       captureAfter('Last Name', flat),
  employeenumber: captureAfter('Employee Number', flat),
  jobtitle:       captureAfter('Job Title', flat),
  department:     captureAfter('Department', flat),
  managersam:     captureAfter('Manager SAM', flat) || captureAfter('Manager', flat),
  officephone:    captureAfter('Office Phone', flat) || captureAfter('Phone', flat),
  officelocation: captureAfter('Office Location', flat) || captureAfter('Location', flat),
  sourceusername: captureAfter('Source Username', flat),
};

// ----- 3) Validate required fields -----
const required = ['firstname','lastname','employeenumber','jobtitle','department','managersam'];
const missing = required.filter(f => !userData[f] || userData[f].trim() === '');
if (missing.length) {
  throw new Error(`Missing required fields: ${missing.join(', ')}. Parsed body was empty or labels didn't match exactly.`);
}

// ----- 4) Subject/From/MessageId from this payload shape -----
const subj =
  $json.subject ||
  ($json.headers && typeof $json.headers.subject === 'string'
    ? $json.headers.subject.replace(/^Subject:\s*/i,'').trim()
    : '');

const fromText =
  ($json.from && $json.from.text) ||
  ($json.headers && typeof $json.headers.from === 'string'
    ? $json.headers.from.replace(/^From:\s*/i,'').trim()
    : '');

const msgId =
  $json.messageId ||
  ($json.headers && typeof $json.headers['message-id'] === 'string'
    ? $json.headers['message-id'].replace(/^Message-Id:\s*/i,'').trim()
    : '');

// ----- 5) Return in your expected shape -----
return {
  body: userData,
  originalEmail: {
    subject: subj,
    from: fromText,
    messageId: msgId,
  },
};

if (!$json["stdout"] || $json["stdout"].trim() === "") {
  throw new Error("No JSON output received from PowerShell script. Stdout was empty.");
}
let out;
try {
  out = JSON.parse($json["stdout"]);
} catch (e) {
  throw new Error("Failed to parse JSON from PowerShell script: " + e.message + "\n\nOutput was:\n" + $json["stdout"]);
}
if (out.error) {
  throw new Error("PowerShell script error: " + out.error);
}
return out;

