// 1. Get the raw output from the previous SSH/PowerShell node
let raw = $input.first().json.stdout;
let events = [];

// 2. Parse the JSON string from PowerShell into a JS object
if (raw && typeof raw === 'string' && raw.trim() !== '') {
  try {
    let parsed = JSON.parse(raw);
    // Ensure events is always an array even if only one lockout is found
    events = Array.isArray(parsed) ? parsed : [parsed];
  } catch (e) {
    // If parsing fails, return an empty array to stop the workflow safely
    return [];
  }
}

// 3. Map the data and fix the timestamp to use the REAL event time
return events.map(e => {
  const eventDate = new Date(e.Timestamp);

  const friendlyTime = eventDate.toLocaleString('en-US', {
    month: 'short',
    day: 'numeric',
    year: 'numeric',
    hour: 'numeric',
    minute: '2-digit',
    hour12: true
  });

  return {
    json: {
      username: e.Username,
      fullname: e.FullName,
      computer: e.CallerComputer || 'UNKNOWN',
      timestamp: friendlyTime 
    }
  };
});

// 1. Setup
const currentLockouts = $input.all();
const staticData = $getWorkflowStaticData('global');
staticData.alreadyAlerted = staticData.alreadyAlerted || {};

const now = Date.now();
const TWO_HOURS = 2 * 60 * 60 * 1000;
const currentUsernameList = currentLockouts.map(item => item.json.username);

// 2. Cleanup Memory
// If a user was previously alerted but is NOT in the current lockout list, 
// they are likely unlocked. Remove them so a future lockout triggers immediately.
for (const user in staticData.alreadyAlerted) {
  if (!currentUsernameList.includes(user)) {
    delete staticData.alreadyAlerted[user];
  }
}

// 3. Filter for New or "Stale" Lockouts
const newLockouts = currentLockouts.filter(item => {
  const user = item.json.username;
  const lastAlertTime = staticData.alreadyAlerted[user];

  // Logic: Alert if we've never seen them, OR if they've been locked for > 2 hours
  if (!lastAlertTime || (now - lastAlertTime) > TWO_HOURS) {
    staticData.alreadyAlerted[user] = now; // Update/Set alert timestamp
    return true;
  }

  return false;
});

return newLockouts;

// 1. Get the list of lockouts from the Aggregate node
const lockouts = $json.data;

// 2. Build the table rows as a clean string
const tableRows = lockouts.map((item, idx) => {
  const bgColor = idx % 2 === 0 ? '#ffffff' : '#f8f9fa';
  return `
    <tr style="border-bottom: 1px solid #e0e0e0; background-color: ${bgColor};">
      <td style="padding: 14px 16px; font-family: sans-serif; font-size: 13px;">${item.username}</td>
      <td style="padding: 14px 16px; font-weight: 500;">${item.fullname || '‚Äî'}</td>
      <td style="padding: 14px 16px;"><span style="background-color: #fadbd8; color: #c0392b; padding: 4px 12px; border-radius: 4px; font-weight: 600; font-size: 12px;">‚ö† Locked</span></td>
      <td style="padding: 14px 16px;">${item.computer || '‚Äî'}</td>
      <td style="padding: 14px 16px; color: #7f8c8d;">${item.timestamp}</td>
    </tr>`;
}).join('');

// 3. Wrap rows in the full template
const fullHtml = `
<div style="font-family: sans-serif; color: #333; max-width: 900px;">
     <div style="background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 24px; border-radius: 8px 8px 0 0;">
         <h2 style="margin: 0;">üîê AD Account Lockouts</h2>
         <p style="margin: 8px 0 0 0; opacity: 0.9;">Live Status Monitor</p>
     </div>
     <div style="background: white; border: 1px solid #e0e0e0; border-radius: 0 0 8px 8px; overflow: hidden;">
         <table style="border-collapse: collapse; width: 100%;">
             <thead>
                 <tr style="background-color: #f8f9fa; border-bottom: 2px solid #e0e0e0;">
                     <th style="padding: 16px; text-align: left;">Username</th>
                     <th style="padding: 16px; text-align: left;">Full Name</th>
                     <th style="padding: 16px; text-align: left;">Status</th>
                     <th style="padding: 16px; text-align: left;">Source</th>
                     <th style="padding: 16px; text-align: left;">Last Check</th>
                 </tr>
             </thead>
             <tbody>${tableRows}</tbody>
         </table>
     </div>
     <div style="background: #f8f9fa; padding: 16px; text-align: right; font-size: 12px; color: #95a5a6;">
         <em>Generated via n8n</em>
     </div>
</div>`;

return { 
  emailHtml: fullHtml,
  lockoutCount: lockouts.length
};

