function sanitize(val) {
  if (!val || typeof val !== 'string') return '';
  return val.replace(/[;'"\`$|&><\\{}()]/g, '').trim();
}

function htmlToText(html) {
  if (!html) return '';
  html = html
    .replace(/<br\s*\/?>/gi, '\n')
    .replace(/<\/(p|div|li|tr|h\d)>/gi, '\n')
    .replace(/&nbsp;/gi, ' ');
  const text = html.replace(/<[^>]+>/g, ' ');
  return text.replace(/\r?\n/g, '\n').replace(/[ \t]+\n/g, '\n').replace(/[ \t]{2,}/g, ' ').trim();
}

let emailBody =
  ($json.text && String($json.text)) ||
  htmlToText($json.html) ||
  htmlToText($json.textAsHtml) ||
  ($json.snippet && String($json.snippet)) ||
  '';

if (!emailBody && $json.payload && $json.payload.body && $json.payload.body.data) {
  try {
    const buff = Buffer.from($json.payload.body.data, 'base64');
    emailBody = buff.toString('utf8');
  } catch (_) {}
}

const flat = (emailBody || '').replace(/\r/g, '').replace(/[ \t]+/g, ' ').trim();

const LABELS = [
  'First Name','Last Name','Employee Number','Job Title',
  'Department','Manager SAM','Manager','Office Phone','Phone',
  'Office Location','Location'
];

function esc(s){ return s.replace(/[.*+?^${}()|[\]\\]/g, '\\$&'); }

function captureAfter(label, src) {
  const labelEsc = esc(label);
  const next = LABELS.filter(l => l !== label).map(esc);
  const lookahead = next.length ? `(?=\\s*(?:${next.join('|')})\\s*:|$)` : '$';
  const re = new RegExp(`${labelEsc}\\s*:\\s*([\\s\\S]*?)\\s*${lookahead}`, 'i');
  const m = src.match(re);
  return m ? m[1].trim() : '';
}

const userData = {
  firstname:      sanitize(captureAfter('First Name', flat)),
  lastname:       sanitize(captureAfter('Last Name', flat)),
  employeenumber: sanitize(captureAfter('Employee Number', flat)),
  jobtitle:       sanitize(captureAfter('Job Title', flat)),
  department:     sanitize(captureAfter('Department', flat)),
  managersam:     sanitize(captureAfter('Manager SAM', flat) || captureAfter('Manager', flat)),
  officephone:    sanitize(captureAfter('Office Phone', flat) || captureAfter('Phone', flat)),
  officelocation: sanitize(captureAfter('Office Location', flat) || captureAfter('Location', flat)),
};

const required = ['firstname','lastname','employeenumber','jobtitle','department','managersam'];
