  throw new Error(`[AD] Missing required fields: ${missing.join(', ')}. Parsed body was empty or labels didn't match.`);
}

const subj = $json.subject || ($json.headers?.subject?.replace(/^Subject:\s*/i,'').trim()) || '';
const fromText = ($json.from?.text) || ($json.headers?.from?.replace(/^From:\s*/i,'').trim()) || '';
const msgId = $json.messageId || ($json.headers?.['message-id']?.replace(/^Message-Id:\s*/i,'').trim()) || '';

return {
  body: userData,
  originalEmail: { subject: subj, from: fromText, messageId: msgId },
  sanitized: true,
  path: 'AD'
};

