const html = $json["data"] || "";

// try to scope to the main <article>, else fall back to whole HTML
const scope = (html.match(/<article[\s\S]*?<\/article>/i) || [html])[0];

// capture paragraphs, headings, and list items
const matches = scope.match(/<(p|h1|h2|li)[^>]*>(.*?)<\/\1>/gis);

let text = matches
  ? matches
      .map(m => m.replace(/<[^>]+>/g, '').trim())
      .filter(Boolean)
      .join('\n\n')
  : 'No article content found.';

return [{ json: { article: text } }];

// Log full input for debugging
console.log("Full input to Validator:", JSON.stringify($json, null, 2));

// Get raw AI output
let raw = $json.output || $json.text || $json.content || "";
console.log("Raw AI output:", raw);

// Ensure it's a string
if (typeof raw !== "string") {
  console.log("Raw is not a string, converting:", typeof raw);
  raw = JSON.stringify(raw);
}
console.log("Raw after string check:", raw);

// Clean the string (remove code blocks, think tags, and normalize dashes)
let s = raw
  .replace(/```(?:json)?\s*([\s\S]*?)```/gi, "$1")
  .replace(/<think>[\s\S]*?<\/think>/gi, "")
  .replace(/â€‘/g, "-") // Replace en-dashes with regular dashes
  .trim();
console.log("Cleaned output:", s);

// Parse JSON
let obj = null;
try {
  obj = JSON.parse(s);
  console.log("Parsed object:", JSON.stringify(obj, null, 2));
  // Handle nested stringified JSON
  while (typeof obj === "string") {
    console.log("Nested string detected, parsing again:", obj);
    s = obj;
    obj = JSON.parse(obj);
  }
} catch (e) {
  console.log("JSON parse error:", e.message, "Input string:", s.slice(0, 500));
}

// Fallback: attempt to extract JSON-like object
if (!obj) {
  console.log("Falling back to regex extraction");
  const m = s.match(/\{[\s\S]*?\}/); // Non-greedy match
  if (m) {
    try {
      obj = JSON.parse(m[0]);
      console.log("Fallback parsed object:", JSON.stringify(obj, null, 2));
    } catch (e) {
      console.log("Fallback JSON parse error:", e.message, "Matched string:", m[0]?.slice(0, 500));
    }
  } else {
    console.log("No JSON-like object found in fallback regex");
  }
}

// Extract title and bullets with fallback
let title = obj && obj.title ? obj.title : "Summary";
let bullets = Array.isArray(obj?.bullets) ? obj.bullets : [];
console.log("Extracted title:", title, "Bullets count:", bullets.length, "Bullets:", bullets);

// Enforce exactly 5 bullets
if (bullets.length > 5) {
  console.log("Truncating bullets from", bullets.length, "to 5");
  bullets = bullets.slice(0, 5);
}
while (bullets.length < 5) {
  bullets.push("No further details available.");
}

return [{ json: { title, bullets, source: $json.source || "Local" } }];

