const CHUNK_SIZE = 3000, OVERLAP = 150, MAX_CHUNKS = 20;
const text = $json.article || "";
const chunks = [];
for (let i = 0; i < text.length && chunks.length < MAX_CHUNKS; i += (CHUNK_SIZE - OVERLAP)) {
  chunks.push({ json: { chunk: text.slice(i, i + CHUNK_SIZE), index: chunks.length + 1 } });
}
return chunks;
